step.params:
const re  = /<meetingName>(.*)<\/meetingName>[\s\S]*?<meetingID>(([\w_-]+)<\/meetingID>[\s\S]*?<isBreakout>(\w+)<\/isBreakout>/g;
const fmt = '{"{#NAME}": "$1", "{#ID}": "$2", "{#ISBREAKOUT}": $3}';
var lld = '[';
var data = value.match(re);
if (data) { lld += data.toString().replace(re,fmt); }
lld += ']';
return lld;
