step.params:
var ret = "[]";
data = value.match(/<meetingName>(.*)<\/meetingName>[\s\S]*?<meetingID>(\w+)<\/meetingID>/g);
if (data) { 
  data = data.toString();
  data = data.replace(/<meetingName>(.*)<\/meetingName>[\s\S]*?<meetingID>(\w+)<\/meetingID>/g,'{"{#NAME}": "$1", "{#ID}": "$2"}'); 
  ret = data.replace(/(.*)/,'[$1]');
}
return ret;
